BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS events (
    event_id INTEGER PRIMARY KEY AUTOINCREMENT,
    title TEXT NOT NULL,
    description TEXT,
    date TEXT,
    year INTEGER,
    location_id INTEGER,
    image_url TEXT,
    source TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, parent_id INTEGER REFERENCES events(event_id), locationName TEXT,
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
CREATE TABLE IF NOT EXISTS favorites (
    favorite_id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    event_id INTEGER NOT NULL,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(user_id),
    FOREIGN KEY (event_id) REFERENCES events(event_id),
    UNIQUE(user_id, event_id)
);
CREATE TABLE IF NOT EXISTS figure_events (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    figure_id INTEGER NOT NULL,
    event_id INTEGER NOT NULL,
    role TEXT,
    FOREIGN KEY (figure_id) REFERENCES historical_figures(figure_id),
    FOREIGN KEY (event_id) REFERENCES events(event_id)
);
CREATE TABLE IF NOT EXISTS historical_figures (
    figure_id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    description TEXT,
    dob DATE,
    dod DATE,
    image_url TEXT
);
CREATE TABLE IF NOT EXISTS locations (
    location_id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT,
    latitude REAL,
    longitude REAL,
    region TEXT
);
CREATE TABLE IF NOT EXISTS password_reset_tokens (
    token_id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    token TEXT UNIQUE NOT NULL,
    expires_at DATETIME,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);
CREATE TABLE IF NOT EXISTS users (
    user_id INTEGER PRIMARY KEY AUTOINCREMENT,
    email TEXT UNIQUE NOT NULL,
    password_hash TEXT NOT NULL,
    full_name TEXT,
    avatar_url TEXT,
    role TEXT DEFAULT 'user',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
INSERT INTO "events" ("event_id","title","description","date","year","location_id","image_url","source","created_at","updated_at","parent_id","locationName") VALUES (1,'Báo Le Paria (Người cùng khổ) ra số đầu tiên tại Pari.','Báo Le Paria (Người cùng khổ) ra số đầu tiên tại Paris vào tháng 4 năm 1922. Đây là cơ quan ngôn luận của Hội Liên hiệp các dân tộc thuộc địa ở Pháp, do Nguyễn Ái Quốc sáng lập và trực tiếp tham gia biên tập. Nội dung báo lên án chính sách áp bức, bóc lột của chủ nghĩa thực dân, tố cáo tội ác của thực dân Pháp ở các thuộc địa, đồng thời kêu gọi đoàn kết đấu tranh giải phóng dân tộc. Le Paria trở thành tiếng nói mạnh mẽ bênh vực quyền lợi của các dân tộc bị áp bức trên thế giới.','192-04-01',192,1,'assets/Image/1_4_0192.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Paris'),
 (2,'Đội danh dự Hà Nội được thành lập. Đây là một đội vũ trang đặc biệt của Đảng trước Cách mạng tháng Tám có nhiệm vụ tuyên truyền xung phong và trừ gian diệt ác.','Đội Danh dự Hà Nội được thành lập trước Cách mạng tháng Tám 1945, là lực lượng vũ trang đặc biệt của Đảng. Đội có nhiệm vụ tuyên truyền xung phong, trừ gian diệt ác, góp phần bảo vệ phong trào cách mạng và chuẩn bị cho Tổng khởi nghĩa Tháng Tám.','1945-04-01',1945,2,'assets/Image/1_4_1945.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Hà Nội'),
 (3,'Ngày truyền thống binh chủng pháo cao xạ.','Ngày truyền thống Binh chủng Pháo cao xạ là 1/4/1953, ngày thành lập Trung đoàn pháo cao xạ đầu tiên của Quân đội nhân dân Việt Nam. Đây là lực lượng phòng không quan trọng, lập nhiều chiến công trong Chiến dịch Điện Biên Phủ và các cuộc kháng chiến sau này, góp phần bảo vệ bầu trời Tổ quốc.','1953-04-01',1953,3,'assets/Image/1_4_1953.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Điện Biên Phủ'),
 (4,'Hồ Chủ tịch đã về thǎm các làng cá và bà con ngư dân trên đảo Tuần Châu, Cát Bà, Cát Hải... ở Quảng Ninh và Hải Phòng. Ngày 18-3-1995, Thủ tướng Chính phủ đã quyết định lấy ngày 1-4 là ngày truyền thống của ngành thuỷ sản nước ta.','Hồ Chủ tịch từng thăm ngư dân ở Tuần Châu, Cát Bà, Cát Hải (Quảng Ninh, Hải Phòng). Ngày 18/3/1995, Thủ tướng Chính phủ quyết định lấy 1/4 là Ngày truyền thống ngành Thủy sản Việt Nam.','1959-04-01',1959,4,'assets/Image/1_4_1959.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Hải Phòng'),
 (5,'Ngày mất giáo sư bác sĩ Đặng Vǎn Ngữ. Ông sinh năm 1910 tại Huế. Ông đã nghiên cứu và sản xuất thành công thuốc kháng sinh, nước lọc Pênixilin chế từ giống nấm, cứu chữa cho thương binh bệnh binh.','Giáo sư, bác sĩ Đặng Văn Ngữ (sinh năm 1910 tại Huế) là nhà y học nổi tiếng, đã nghiên cứu và sản xuất thành công Penicillin từ nấm, góp phần cứu chữa thương binh, bệnh binh trong kháng chiến. Ông hy sinh ngày 1/4/1967 tại chiến trường Trị – Thiên.','1967-04-01',1967,5,'assets/Image/1_4_1967.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Huế'),
 (6,'Sư đoàn 320 A tiến quân theo đường số 7 cùng lực lượng vũ trang tỉnh Phú Yên giải phóng thị xã Tuy Hoà và tỉnh này. Cùng ngày sư đoàn 3 chủ lực quân khu 5 và sư đoàn 968 cơ động trên đường số 19 giải phóng thị xã Quy Nhơn và tỉnh Bình Định.','Ngày 1/4/1975, Sư đoàn 320A phối hợp với lực lượng vũ trang tỉnh Phú Yên tiến công theo đường số 7, giải phóng thị xã Tuy Hòa và toàn tỉnh Phú Yên. Cùng ngày, Sư đoàn 3 chủ lực Quân khu 5 và Sư đoàn 968 cơ động trên đường số 19 đã giải phóng thị xã Quy Nhơn và tỉnh Bình Định, góp phần mở rộng vùng giải phóng ở Khu 5, tạo thế chiến lược quan trọng cho cuộc Tổng tiến công và nổi dậy mùa Xuân 1975.','1975-04-01',1975,6,'assets/Image/1_4_1975.png
',NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Phú Yên'),
 (7,'Giáo sư Đào Duy Anh đã từ trần. Ông sinh nǎm 1904 ở Thanh Hoá. Là nhà sử học, nhà vǎn hoá lớn của nước ta, ông có nhiều công trình khoa học như: Hán - Việt từ điển (nǎm 1932), Pháp - Việt từ điển (nǎm 1936), Việt Nam vǎn hoá sử cương (nǎm 1936), Lịch sử Việt Nam (nǎm 1955), Từ điển truyện Kiều (nǎm 1974).',NULL,'1988-04-01',1988,7,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,'Bình Định'),
 (8,'Nhạc sĩ Trịnh Công Sơn, một trong những nhạc sĩ lớn nhất của tân nhạc Việt Nam, qua đời tại Thành phố Hồ Chí Minh vì bệnh tiểu đường.',NULL,'2001-04-01',2001,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (9,'Ngày sinh Đồng chí Nguyễn Lương Bằng tại xã Thanh Tùng, huyện Thanh Miện, tỉnh Hải Dương, trong một gia đình nho giáo thanh bạch. Sau Cách mạng Tháng Tám, đồng chí đã giữ nhiều trọng trách: Ủy viên Ban Chấp hành Trung ương Đảng, Trưởng ban kiểm tra Trung ương, Đại sứ Việt Nam đầu tiên tại Liên Xô, Tổng thanh tra của Chính phủ, Phó Chủ tịch nước Cộng hoà XHCN Việt Nam và Quyền Chủ tịch nước CHXHCN Việt Nam. Đồng chí Nguyễn Lương Bằng từ trần nǎm 1979.',NULL,'1904-04-02',1904,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (10,'Ngày sinh Huỳnh Vǎn Nghệ tại làng Tân Tịch, huyện Tân Uyên, nay thuộc tỉnh Bình Dương. Trong kháng chiến chống Pháp, ông làm phó khu trưởng khu 7 (miền đông Nam Bộ), chỉ huy nhiều trận đánh nổi tiếng với bí danh Tám Ngãi, được phong quân hàm Thiếu tướng. Sau ngày hoà bình lập lại, ông chuyển ngành, giữ chức Thứ trưởng Bộ Lâm nghiệp. Ông qua đời ngày 5-3-1977 tại thành phố Hồ Chí Minh.',NULL,'1914-04-02',1914,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (11,'Báo Tin tức - Cơ quan của Mặt trận dân chủ ra số đầu tiên. Tờ báo do đồng chí Trường Chinh phụ trách ban Tuyên truyền cổ động của TW Đảng Cộng sản Đông Dương đứng ra làm chủ nhiệm.',NULL,'1938-04-02',1938,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (12,'Sư đoàn 10 và trung đoàn 25 bộ binh của ta đã tiêu diệt cụm phòng thủ của trung đoàn 40 (thuộc sư đoàn 22 ngụy), giải phóng quận lỵ Khánh Dương, tiêu diệt lữ đoàn 3 (thuộc sư đoàn dù) ở đèo (Phượng Hoàng), mở thông đường xuống Ninh Hoà, rồi theo đường số 21, tiến xuống giải phóng thành phố Nha Trang và quân cảng Cam Ranh. Cùng ngày, ta còn giải phóng Bình Long.',NULL,'1975-04-02',1975,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (13,'Tại thủ đô Phnôm Pênh, Bộ Quốc phòng nước CHND Campuchia và Hội đồng toàn quốc mặt trận đoàn kết xây dựng và bảo vệ Tổ quốc Campuchia đã tổ chức trọng thể lễ trao tặng Huân chương CHND Campuchia cho binh đoàn 52 và Huân chương Bảo vệ Tổ quốc hạng nhất cho các đoàn 7703, 7706, 9906 quân tình nguyện Việt Nam.',NULL,'1985-04-02',1985,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (14,'Đào Tấn sinh ra ở xã Phúc Lộc, huyện Tuy Phước, tỉnh Bình Định và từ trần ngày 15-7-1907. Cuộc đời ông có nhiều nét đặc biệt: 3 lần làm tổng đốc, 4 lần làm thượng thư và là một nghệ sĩ lớn nhất của dân tộc ở thế kỷ XIX.',NULL,'1845-04-03',1845,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (15,'Biên đội không quân Phạm Ngọc Lan bắn rơi 2 máy bay phản lực hiện đại F8 trên vùng trời Hàm Rồng Thanh Hoá.',NULL,'1965-04-03',1965,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (16,'Bộ trưởng Quốc phòng Melvin Laird thông báo Hoa Kỳ sẽ bắt đầu nỗ lực Việt Nam hóa chiến tranh.',NULL,'1969-04-03',1969,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (17,'Nước ta quyết định tham gia Công ước về ngǎn ngừa và trừng trị tội ác diệt chủng, và Công ước về ngǎn ngừa và trừng trị tội ác của chủ nghĩa Apácthai.',NULL,'1981-04-03',1981,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (18,'Ngày mất Võ Duy Thanh. Ông sinh nǎm 1807, quê ở làng Bồng, huyện Yên Khánh, tỉnh Ninh Bình. Ông tuy đỗ bảng nhãn nhưng tài đáng Trạng Nguyên nên người đương thời gọi ông là Trạng Bồng.',NULL,'1861-04-04',1861,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (19,'Máy bay Mỹ ném bom, bắn rốc két xuống xã Quang Trung, huyện Quảng Xương, tỉnh Thanh Hoá.',NULL,'1965-04-04',1965,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (20,'Thành phố Đà Lạt và tỉnh Lâm Đồng được giải phóng.',NULL,'1975-04-04',1975,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (21,'Công nhân nhà máy Ba Son (Sài Gòn) tiến hành bãi công. Những yêu sách mà công nhân đưa ra trong cuộc bãi công bao gồm: Cải thiện giờ làm việc - Tǎng lương và phụ cấp - Tuyển lại công nhân bị chủ sa thải...',NULL,'1937-04-05',1937,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (22,'Công bố Pháp lệnh phòng cháy chữa cháy. Pháp lệnh này đã được ủy ban thường vụ Quốc hội nước ta thông qua ngày 23-3-1963. Đây là vǎn bản đầu tiên có tính chất pháp quy về công tác phòng cháy chữa cháy.',NULL,'1963-04-05',1963,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (23,'Nguyễn Bá Ngọc hi sinh. Nguyễn Bá Ngọc (1952-1965) là học sinh lớp 4 b năm học 1964- 1965 tại xã Quảng Trung huyện Quảng Xương, tỉnh Thanh Hóa. Ngày 4-4-1965, máy bay Mỹ đã tới ném bom, bắn phá xã Quảng Trung, ngồi dưới hầm trú ẩn, nghe có tiếng gọi mình, Nguyễn Bá Ngọc vội chạy lên cứu được hai em nhỏ hàng xóm khỏi bom đạn thù, em bị một bắn vào lưng đã hi sinh.',NULL,'1965-04-05',1965,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (24,'Từ ngày 5-4 đến 7-4-1972, bộ đội ta liên tục tấn công địch ở thị xã Lộc Ninh (Bình Long), đã đánh gục sư đoàn 5, diệt trung đoàn thiết giáp 1 của ngụy. Cả chiến đoàn 69 đầu hàng ta. Thị xã Lộc Ninh với 28 nghìn dân được giải phóng.',NULL,'1972-04-05',1972,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (25,'Mẻ than cốc đầu tiên được chế luyện từ loại than gầy Quảng Ninh đã ra lò tại Khu gang thép Thái Nguyên. Với công suất 6,7 tấn than cốc/nǎm.',NULL,'1982-04-05',1982,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (26,'Ngày sinh của Nguyễn Bỉnh Khiêm. Ông sinh ra ở làng Trung An - huyện Vĩnh Lại - tỉnh Kiến An cũ (nay là thôn Trung An - xã Lí Học - Vĩnh Bảo - Hải Phòng). Ông là một người thầy toàn diện, được xếp là một trong 13 danh nhân vǎn hoá lớn nhất của lịch sử dân tộc.',NULL,'1491-04-06',1491,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (27,'Hội nghị cán bộ TW Đảng lần thứ 2 Họp tại chiến khu miền Bắc. Đây là hội nghị đầu tiên từ sau khi Trung ương rút khỏi Thủ đô lên cǎn cứ địa Việt Bắc để chỉ đạo cả nước kháng chiến.',NULL,'1947-04-06',1947,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (28,'Ních xơn đã huy động lực lượng lớn không quân, hải quân ồ ạt tham chiến ở miền Nam và đánh phá trở lại miền Bắc.',NULL,'1972-04-06',1972,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (29,'Ở miền Bắc nước ta đã tiến hành bầu cử Quốc hội khoá 5. Tổng số đại biểu đã bầu là 242 đại biểu.',NULL,'1975-04-06',1975,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (30,'Ngô Thì Nhậm từ trần. Ông sinh nǎm 1746 tại làng Tả Thanh Oai, huyện Thanh Trì, Hà Nội. Ông hiến diệu kế rút quân về Tam Điệp, góp phần đại thắng quân Thanh mùa xuân nǎm Kỷ Dậu 1789.',NULL,'1803-04-07',1803,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (31,'Ngày sinh Đồng chí Lê Duẩn, ở xã Triệu Thành, huyện Triệu Phong, tỉnh Quảng Trị. Đồng chí giữ nhiều chức vụ quan trọng: Bí thư Xứ ủy nam Kỳ, Bí thư Trung ương Cục miền Nam, Ủy viên Bộ Chính trị, Tổng Bí thư ban chấp hành Trung ương Đảng. Đồng chí qua đời ngày 10-7-1986 tại Hà Nội.',NULL,'1907-04-07',1907,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (32,'Học sinh trường quốc học Huế tổ chức bãi khoá do thái độ miệt thị của một giáo sư thực dân đối với học sinh. Sự kiện này đã gây tiếng vang của Trung Kỳ và cả nước. Từ cuộc bãi khoá này, nhiều học sinh ở trường đã bước vào con đường hoạt động cách mạng như: Võ Nguyên Giáp, Hải Triều...',NULL,'1927-04-07',1927,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (33,'Hồ Chủ tịch ký sắc kệnh thành lập bộ đội địa phương. Với Sắc lệnh này, các đội du kích tập trung được phát triển thành các trung đội, đại đội bộ đội địa phương huyện và các tiểu đoàn bộ đội địa phương tỉnh.',NULL,'1949-04-07',1949,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
 (34,'Tổng thống Hoa Kỳ R.Nixơn tuyên bố sẽ rút một đợt 10.000 binh lính Mỹ khỏi Việt Nam. Ngay sau đó, cuộc biểu tình quần chúng Mỹ đã nổ ra ở thủ đô Oasinhtơn với quy mô chưa từng có đòi chấm dứt ngay chiến tranh lập lại hoà bình ở Việt Nam và trên bán đảo Đông Dương.',NULL,'1971-04-07',1971,NULL,NULL,NULL,'2025-09-30 03:24:53','2025-09-30 03:24:53',NULL,NULL),
